            Insert.IntoTable(TableName.Job).WithIdentityInsert().Row(new { Id = 653, CreatedAt = DateTime.Parse("2018-01-27"), IsActive = true, Title = "Software Operations Manager", Description = "<p></p><p>Software Operations Manager</p><br><p><br></p><br><p>About the Role </p><br><p>We are looking for a passionate, dynamic professional to join our team and ensure that active projects are running as efficiently as possible, to meet the needs of both the organization and our clients. &nbsp;</p><br><p>This is a full-time, <b>remote</b> , home-based position.</p><br><p>The <b>Software Operations Manager</b> will exercise leadership skills to promote the company's culture, provide guidance to project managers and ultimately contribute to the strategic development of the company's day-to-day operations.</p><br><p>This is an ideal position for motivated individuals who would like to join a diverse, fast-paced environment. &nbsp;</p><br><p>Successful candidates will be:</p><br><ul><br><li>Experts at self-organization, prioritization and multi-tasking. This is essential to succeed in our dynamic environment.</li><br><li>Industrious and energetic. &nbsp;</li><br><li>Detail-oriented. Details matter.</li><br><li>Receptive to constructive feedback and interested in constant growth and self-improvement. </li><br><li>Able to manage stress and maintain a positive attitude, even in challenging situations. </li><br><li>Perceptive and understanding with the ability to communicate with others in a clear and professional manner. </li><br><li>Highly adept at risk prevention and problem solving.</li><br><li>Operationally independent eleven given the necessary training and support. &nbsp;</li><br><li>A Software Development Lifecycle expert. Experience with how software applications are built and associated best practices is essential.</li><br></ul><br><p>Responsibilities: </p><br><ul><br><li>Support the onboarding of new projects to effectively meet client expectations.</li><br><li>Once onboarded, monitor active projects to identify and prevent risks, ensure effective implementation and timely delivery within project budgets.</li><br><li>Address concerns escalated by either project managers or clients.</li><br><li>Interview, hire, train, manage, mentor and project managers. </li><br><li>Support Account Management as they work with our client to maximize our services.</li><br><li>Liaise with Accounting / Finance to analyze and improve project profitability. </li><br><li>Work with HR on employee performance reviews and associated operational actions.</li><br><li>Suggest and implement SDLC and other process improvements.</li><br><li>Work effectively with the Director of Engineering when troubleshooting issues and when working on longer-term process improvements.</li><br></ul><br><p>Required Qualifications: </p><br><ul><br><li>Bachelor's degree in a business or technical field. Master's degree highly preferable.</li><br><li>1+ years of experience in full-cycle software project management. </li><br><li>Excellent English communication skills, both verbal and written. </li><br><li>Demonstrated leadership skills and the aptitude to learn new skills quickly. </li><br><li>Demonstrated risk and client management expertise.</li><br><li>Excellent computer skills and proficient in Microsoft Office. </li><br></ul><br><p><br></p><br><p>About the Company </p><br><p>Founded in 2006, US-based Scope Software is a leading provider of quality, economical custom solutions for web, mobile and desktop. With over 200 team members in more than 30 countries, we are the largest virtual software services company in the world. We might be a global company, but we cater to specific, local needs. Nothing inspires us more than the challenge of finding the right solution for a complicated problem. Above all we are dedicated to building unique applications for our clients, who are primarily located in North America &amp; Europe.</p><br><p>Since our establishment, we have seen the growth and strive to make an impact in the world of software development. Our company has matured we have stayed focused on what really matters: attracting and retaining global talent, developing dynamic solutions that leverage the latest technologies and maintaining successful partnerships with our clients.</p><br><p>While we sincerely appreciate all applications, only those candidates selected for screening will be contacted.</p><br><p><br></p><br><p>This is a full-time remote position.</p><br><p>Hourly rate range is $ 18- $ 30 depending upon skills and experience.</p><br><br><br><br><p></p>", HowToApply = "<b>Please apply online: </b> <a target=\"_blank\" rel=\"nofollow\" href=\"https://scopicsoftware.recruiterbox.com/jobs/8ef07e1dc4fd4bb39679428ed07d0d41\">https://scopicsoftware.recruiterbox.com/jobs/8ef07e1dc4fd4bb39679428ed07d0d41</a>&nbsp;", ViewCount = 305, Likes = 1, IsRemote = false, IsHidden = false, Approved = true, PublishedDate = DateTime.Parse("2018-01-27"), CategoryId = 0, CompanyId = 51, HireTypeId = 4, LocationId = 595, UserId = 161 });
