            Insert.IntoTable(TableName.Job).WithIdentityInsert().Row(new { Id = 678, CreatedAt = DateTime.Parse("2018-02-13"), IsActive = true, Title = "graphic designer", Description = "<p></p><p>We are looking for a self-motivated and energetic freelance or part-time remote graphic designer who has outstanding graphic design skills, to join our team. Applicants should be able to develop the overall layout and production design for advertisements, brandings, print and online marketing materials including brochures, flyers, banners, posters, ads, booklets, and other media for the DR tourism and events industry.</p><p></p><p><b>Portfolio is required.</b></p><p></p><b>Responsibilities</b><p><b>Graphic Designer Job Duties:</b></p><p></p><ul><li>Prepares work to be accomplished by gathering information and materials.</li><li>Plans concept by studying information and materials.</li><li>Illustrates concept by designing rough layout of art and copy regarding arrangement, size, type size and style, and related aesthetic concepts.</li><li>Obtains approval of concept by submitting rough layout for approval.</li><li>Prepares finished copy and art by operating typesetting, printing.</li><li>Contributes to team effort by accomplishing related results as needed.</li><li>Collaborate with the marketing team to develop marketing and communications plans that leverage the social media space.</li></ul><div><br></div><b>Qualifications</b><p><b>Requirements:</b></p><ul><li>2+ years direct experience</li><li>Outstanding graphic design skills</li><li>Positive attitude</li><li>Expert knowledge of Adobe Creative Suite especially Photoshop, Illustrator and InDesign, MS Office, PowerPoint</li><li>Be able to communicate in Spanish and English.</li><li>Ability to work under pressure and deliver on time.</li><li>Great collaboration skills, self-motivated and able to work independently.</li><li>Simplify the complex</li><li>Desire to continue growing abilities in respective field</li><li>Impressive portfolio and great taste in design</li><li>Previous work experience in the tourism sector</li></ul><p><b>Bonus Skills:</b></p><p><b></b></p><ul><li>Experience with social media platforms, including, but not limited to Facebook, Google Plus, Pinterest, Twitter, YouTube and etc.</li><li>Experience writing, editing and crafting content for the social media space</li></ul><p></p><p><b>Important Qualities:</b></p><p></p><p><b>Artistic ability:</b>&nbsp;Graphic designers must be able to create designs that are artistically interesting and appealing to clients and consumers. They produce rough illustrations of design ideas, either by hand sketching or by using a computer program.</p><p></p><p><b>Communication skills:</b>&nbsp;Must be able to communicate with the team, clients, art director and other designers to ensure that their designs accurately reflect the desired message and effectively express information.</p><p></p><p><b>Computer skills:</b>&nbsp;Must be able to use specialized graphic design software to prepare their designs. (Photoshop, Illustrator, InDesign, MS Office, Keynote or PowerPoint)</p><p></p><p><b>Creativity:</b>&nbsp;Must be able to think of new approaches to communicating ideas to consumers and be able to develop unique designs that convey a recognizable meaning on behalf of clients.</p><p></p><p><b>Time-management skills:</b>&nbsp;Must be able to work on multiple projects at the same time, each with a different deadline.</p><br><p></p>", HowToApply = "<p>please send your CV and portfolio of design work</p>", ViewCount = 319, Likes = 0, IsRemote = true, IsHidden = false, Approved = true, PublishedDate = DateTime.Parse("2018-02-13"), CategoryId = 1, CompanyId = 9, HireTypeId = 2, LocationId = 617, UserId = 493 });
            Insert.IntoTable(TableName.Job).WithIdentityInsert().Row(new { Id = 680, CreatedAt = DateTime.Parse("2018-02-16"), IsActive = true, Title = "Project Manager", Description = "<p></p><p>ducación: Licenciada en área de negocios o ingeniera en sistema con cursos o especialidad en manejo de proyectos.</p><p>Habilidades para la posición:</p><p></p><ul><li>Identificar y ordenar las etapas de un proyecto<br></li><li>Entender los flujos de trabajo<br></li><li>Organización<br></li><li>Buena Comunicación <br></li><li>Visualizar posibles problemas y adelantarse a ellos<br></li><li>Liderazgo</li></ul><p></p><p>Responsabilidades:</p><p></p><ul><li>Planificación de los proyectos de acuerdo a los objetivos del negocio <br></li><li>Creación del plan de trabajo</li><li>Estimación de tiempos y manejo de recursos</li><li>Comunicación entre los departamentos y equipos que intervienen en el proyecto</li><li>Cierre de proyectos:<ul><li>Test scripts</li><li>Presentación de entregables establecidos en el proyecto</li></ul></li><li>Control de facturación y costo de los proyectos.</li></ul><p></p><p><br></p><p>Interesados enviar correo a loyda.delacruz@gfrmedia.com / guillermo.carrion@gfrmedia.com</p><br><p></p>", HowToApply = "<p>Interesados enviar correo a empleosrd@gfrmedia.com<br></p>", ViewCount = 277, Likes = 0, IsRemote = false, IsHidden = false, Approved = true, PublishedDate = DateTime.Parse("2018-02-16"), CategoryId = 8, CompanyId = 23, HireTypeId = 4, JoelTestId = 347, LocationId = 619, UserId = 511 });
