            Insert.IntoTable(TableName.Job).WithIdentityInsert().Row(new { Id = 690, CreatedAt = DateTime.Parse("2018-03-01"), IsActive = true, Title = "IT QA Analyst", Description = "<p><br>o  This position participates directly in manual and automated software test guidelines and standards. <br>o  The Test Analyst will analyze business, technical and functional requirements transferring them into supporting test plans and execute those test plans while adhering to software test guidelines and standards. <br>o  The Test Analyst will participate as a project team member with Business Analysts, Developers and interface partners and will provide direction and support to an extended team of on and offshore QA Analysts.  <br>o  The Test Analyst should have experience with smoke, regression and automation testing practice framework as well as exposure to automation tools, preferably Oracle Automated Testing Suite (OATS).<br>o  Evaluates and tests new or modified software programs and software development procedures to ensure that programs developed throughout the company function according to user requirements and conform to established guidelines. <br>o  Support responsibilities will include the development and execution of test scripts for new and/or modified software applications and software development procedures and standards.<br>o  Performs tests of software to ensure proper operation and freedom from defects. <br>o  Documents and works to resolve all problems and reports progress on problem resolution to management.<br>o  May review new or modified programs, including documentation, diagram, and flow chart, to determine if the program (a) performs according to the user request; and (b) conforms to guidelines.<br>o  Sets up tests at the request of users to locate and correct program operating errors following the installation of programs.<br>o  Assists the Programmers and Business analysts in developing application test plans and verifying that applications and programs have been tested according to plan.<br>o  Develop automated scripts for regression, load, and performance testing.<br>o  Maintain the perspective of the end-user to ensure that the software will function optimally in the business process environment.<br>o  Generate suitable test data that validate the critical requirements and business rules of the application under test to expose regressive defects.<br><br><br><br>*Job Qualifications<br><br>•  Generally requires BS Degree in an IT or business field or equivalent work experience<br>•  Typically has 1-4 years of directly related experience.<br>•  Knowledge on Oracle financials (AP, AR,GL, FA,..) <br>•  Knowledge of Java and the Oracle testing suite supporting Oracle – OATS is a plus.<br>•  Working knowledge of quality assurance and acceptance testing methodologies.<br>•  Read, analyze, and interpret general business periodicals, professional journals, technical procedures, or government regulations.<br>•  Good interpersonal and communication skills.<br>•  Understand the concepts of structured testing and development methodologies.<br>•  Basic knowledge of test case design techniques such as all-pairs, equivalence partitioning, and boundary value analysis.<br>•  Collaborate closely with US &amp; offshore QA Analysts and software development teams.<br><br><br></p>", HowToApply = "<p>If you're interested email your resume to Erick Cruz (ecruz@synergiesfirst.com). Further info at 829-755-8009.</p>", ViewCount = 304, Likes = 1, IsRemote = false, IsHidden = false, Approved = true, PublishedDate = DateTime.Parse("2018-03-01"), CategoryId = 8, CompanyId = 57, HireTypeId = 4, JoelTestId = 351, LocationId = 627, UserId = 416 });
            Insert.IntoTable(TableName.Job).WithIdentityInsert().Row(new { Id = 691, CreatedAt = DateTime.Parse("2018-03-02"), IsActive = true, Title = "IT Financial Analyst", Description = "<p><br></p><p><b>ATTENTION</b>: <i>WE'RE LOOKING FOR AN ACCOUNTING OR FINANCE PROFESSIONAL WITH 2+ YEARS OF EXPERIENCE IN PLANNING &amp; BUDGETING WHO'S WILLING TO LEARN IT PROCESSES AND SERVICES. <br></i></p><p><i>BELOW MORE DETAILS:<br></i></p><p>The Financial Analyst will help drive the monthly and quarterly <br>financial forecasting and reporting process and perform value-added <br>analysis to assist in decision making. This position is responsible for <br>identifying business risks and opportunities and seeking process <br>improvement in the core functions of the department.</p><br><br><p>Essential Functions:<br>• Analyze monthly financial results and summarize results of operations.<br>•<br> Organize, prepare, standardize and improve efficiency of monthly and <br>quarterly earnings processes as it relates to reports and analysis that <br>the department performs in support of these functions.<br>• Assist <br>in managing the design/development of new management <br>reports; and ensuring the data integrity of all management reports <br>produced in the group.<br>• Create executive-quality PowerPoint <br>presentations to communicate results of quarterly forecasts, results of <br>ad hoc analyses, and to present information to our various stakeholders.</p><p><br></p><h1>Job Qualifications:</h1><br><br><p>• Bachelor’s degree in Finance, Accounting or equivalent combination of education and experience<br>•<br> 2-4 years of hands-on experience performing forecasting/budgeting, <br>performing variance analysis, developing sophisticated financial models <br>while working within a Financial Planning &amp; Analysis department.<br>• Experience using formal budgeting/forecasting tools and ERP systems; Hyperion Planning and Oracle Essbase preferred.<br>•<br> Finance and Accounting – Experience in finance, economic and accounting<br> principles and practices, the financial markets, financial statements, <br>valuation and the analysis and reporting of financial data.<br>• Computer Skills – Advanced on Microsoft Office Products, particularly Excel and PowerPoint.<br>• Good communication English skills (written &amp; verbal). <br></p><br><br><br><p></p><br><br><br><p></p><br><br>", HowToApply = "<p>Email your resume to&nbsp; IT Account Manager Erick Cruz (ecruz@synergiesfirst.com).<br></p>", ViewCount = 232, Likes = 1, IsRemote = false, IsHidden = false, Approved = true, PublishedDate = DateTime.Parse("2018-03-02"), CategoryId = 8, CompanyId = 57, HireTypeId = 4, LocationId = 628, UserId = 416 });
